import yaml
from typing import Any, Dict, List, Tuple

RvizDisplay = Dict[Any, Any]

def get_rviz_config(additional_displays: List[RvizDisplay]):
  return yaml.safe_dump(
    {
      **panels_config, 
      **visualization_manager([
        laser_scan,
        clock_sync_signal,
        tf,
        robot,
        grid,
        *additional_displays,
      ]),
      **window_geometry
    }, 
    default_flow_style=False
  )

def visualization_manager(displays: List[RvizDisplay]):
  visualization_manager = yaml.safe_load(f"""
Visualization Manager:
  Class: ""
  Displays: []
  Enabled: true
  Global Options:
    Background Color: 48; 48; 48
    Fixed Frame: map
    Frame Rate: 30
  Name: root
  Tools:
    - Class: rviz_default_plugins/MoveCamera
    - Class: rviz_default_plugins/FocusCamera
    - Class: rviz_default_plugins/Measure
      Line color: 128; 128; 0
    - Class: rviz_default_plugins/SetInitialPose
      Covariance x: 0.0
      Covariance y: 0.0
      Covariance yaw: 0.0
      Topic:
        Depth: 5
        Durability Policy: Volatile
        History Policy: Keep Last
        Reliability Policy: Reliable
        Value: /initialpose
    - Class: nav2_rviz_plugins/GoalTool
  Transformation:
    Current:
      Class: rviz_default_plugins/TF
  Value: true
  Views:
    Current:
      Class: rviz_default_plugins/ThirdPersonFollower
      Distance: 5.00
      Enable Stereo Rendering:
        Stereo Eye Separation: 0.05999999865889549
        Stereo Focal Distance: 1
        Swap Stereo Eyes: false
        Value: false
      Focal Point:
        X: 0
        Y: 0
        Z: 0
      Focal Shape Fixed Size: true
      Focal Shape Size: 0.05000000074505806
      Invert Z Axis: false
      Name: Current View
      Near Clip Distance: 0.009999999776482582
      Pitch: 0.330397
      Target Frame: base_link
      Value: ThirdPersonFollower (rviz)
      Yaw: 3.1415
    Saved: ~
""")
  visualization_manager['Visualization Manager']['Displays'] = displays
  return visualization_manager

panels_config = yaml.safe_load("""
Panels:
  - Class: rviz_common/Displays
    Help Height: 78
    Name: Displays
    Property Tree Widget:
      Expanded: []
      Splitter Ratio: 0.5
    Tree Height: 659
  - Class: rviz_common/Selection
    Name: Selection
  - Class: rviz_common/Tool Properties
    Expanded:
      - /2D Goal Pose1
      - /Publish Point1
    Name: Tool Properties
    Splitter Ratio: 0.5886790156364441
  - Class: rviz_common/Time
    Experimental: true
    Name: Time
    SyncMode: 1
    SyncSource: Clock Sync Signal""")

def marker_array_display(topic: str, name: str) -> RvizDisplay:
  return yaml.safe_load(f"""
Class: rviz_default_plugins/MarkerArray
Enabled: true
Name: {name}
Namespaces:
  "": true
Topic:
  Depth: 5
  Durability Policy: Volatile
  History Policy: Keep Last
  Reliability Policy: Best Effort
  Value: {topic}
Value: true""")

