from typing import Sequence, Tuple

from .path_follower_base import PathFollowerBase, Point2D, AngularVelocity, LinearVelocity
from .ros_node import expose_as_ros_node

class ExampleFollower(PathFollowerBase):
    def update_path(self, path: Sequence[Point2D]):
        pass

    def get_commands(self, car_position: Point2D, car_bearing: float) -> Tuple[LinearVelocity, AngularVelocity]:
        return 3.0, -0.2

def main():
    expose_as_ros_node(ExampleFollower(), 'example_path_follower')
